var $http = require('./http.js');

module.exports = function(data, callback) {
  	config = {
  		method: 'POST',
  		path: '/user/authenticate',
  		data: data
  	}

  	$http(config)
  	.then(function(res){
  		callback(null,(res.rows?res.rows:res))
  	})
  	.catch(function(error){
  		callback(error,null);
  	})
}
