var validate = require('validator');
var $http     = require('./http.js');
var url      = '/model/show/';
var config   = {
  method: 'GET',
  path: undefined,
  params: []
}

module.exports = function (model, data, next) {
  var modelName = model.charAt(0).toUpperCase() + model.substring(1);
  config.path   = url + modelName + '?' + "column=name";

  $http(config)
  .then(function (data1) {
    console.log(data, data1, config, 'after query');
    next(true);
  })
  .catch(function (err){
    console.log(data, config, 'ERROR', err);
    next(false);
  });
}