'use strict';
var api = require('../')
describe('list', function () {
  it('should list correctly', function (done) {
  	setTimeout(function(){
			var USER = new api.model("appuser");

			USER.find({id:1448})
			.populate("profileId")
			.populate("companyId")
			.exec(function(err,data){
				if(err){
					console.log(err);
				}else{
					data[0].email.should.equal("neilm@meditab.com")
					console.log("ANOTHER SUCCESS",data)

					done()
				}
			})
		},1000)
   	// var onePlusOne = 1 + 1;
   	// onePlusOne.should.equal(2);
   	// must call done() so that mocha know that we are... done.
   	// Useful for async tests.
   	// done();
 	});
});